mapper.insert:
insert into member
values (
	MEMBER_SEQ.nextval
	, #{member_id}
	, #{member_password}
	, #{member_name}
	, #{member_phone}
	, #{member_address}
	, sysdate
	, #{memberImage_saveAddress}
)

mapper.update:
update member
set
	member_id = #{member_id}
	, member_password = #{member_password}
	, member_name = #{member_name}
	, member_phone = #{member_phone}
	, member_address = #{member_address}
	, member_memo = #{member_memo}
	, member_logindate = sysdate
where
	member_num = #{member_num}
